import React, { useReducer, useState } from 'react'

const HookUseReducer = () => {
  //1-comando com usereducer
  const[number,dispatch]= useReducer((state,action)=>{
    return Math.random(state);
  })
//2 avançando no useReducer
const initalTalks =[
  {id:1, text:"Fazer alguma coisa"} ,
  {id:2, text:"Fazer outra coisa"} 
]

const taskReducer = (state,action)=>{
  switch(action.type){
    case"ADD":
        const newTask = {
          id: Math.random(),
          text:taskText
        };
        setTaskText("");
        return[...state,newTask];
        case"DELETE":
          return state.filter((task)=> task.id !== action.id)   
        default:
          return state;
  }
}
const[taskText,setTaskText] = useState("");
const [tasks,dispatchTasks] = useReducer(taskReducer,initalTalks);

const handleSubmit = (e)=> {
  e.preventDefault();

  dispatchTask({ type: "ADD" });
  
  dispatchTasks()
}

const removeTask = (id)=>{
  dispatchTask({ type: "REMOVE", id: id });
}

  return (
    <div>
      <h2>useReducer</h2>
      <p>Numero:{number}</p>
      <button onClick={dispatch}>Alterar Numeros!</button>
      <form onSubmit={handleSubmit}>
        <input type="text" 
        onChange={(e)=>
        setTaskText(e.target.value)}
        value={taskText}/>
        <input type="submit" value="Enviar"/>
       
      </form>
      <h3>Tasrefas</h3>
        {tasks.map((task)=>(
          <li key={task.id} 
          onDoubleClick={()=> 
            removeTask(task.id)}>
              {task.text}</li>
        ))}
      <hr/>
    </div>
  )
}

export default HookUseReducer
