import {useEffect,useState} from 'react'

const HookUseEffect = () => {
    //1 useEffect sem dependencias
    useEffect(()=>{
        console.log("Estou sendo Executado")
    })
    const[number,setNumber] = useState(1)
    const changeSmething = () => {
      setNumber(number +1)
    }
    // 2- Array de deps. vazio
    useEffect(()=>{
      console.log("Serei Executou apenas uma vez ")
    },[])
    //3- Item no array de deps
    const[anotheNumber,setAnotheNumber] = useState(0);
    useEffect(()=>{
      if(anotheNumber > 0){
        console.log("Sou Executado apenas quando o anotheNumbe muda!")
      }
    },[anotheNumber]);
    //4 - cleanup do useEffect
    useEffect(()=>{
      const timer = setTimeout(()=>{
        console.log("Hello Word")
      })
    })
  return (
    <div>
        <h2>useEffect</h2>
        <p>Number:{number} </p>
        <button onClick={changeSmething}>Executar</button>
        <p>Another Number:{anotheNumber}</p>
        <button onClick={()=>setAnotheNumber(anotheNumber +1)}>Mudar Another</button>
        <hr/>
    </div>
  )
}

export default HookUseEffect
