import { useEffect,useRef ,useDebugValue} from "react";

export const usePrevious = (value) =>{
    const ref = useRef;

    useDebugValue("---Custom Hooks E Debug Value")
    useDebugValue("O numero anterior é " + value)
    useEffect(()=>{
        ref.current = value;
    });
    return ref.current;
}
