import React, { useEffect, useLayoutEffect, useState } from 'react'

const HookUselayoutEffect = () => {
    const[name,setName]= useState("Algum Nome")

    useEffect(()=>{
        console.log("2")
        setName("Mudou de novo!")
    },[]);

    useLayoutEffect(()=>{
        console.log("1")
        setName("Outro Nome")
    },[]);
    console.log(name)
  return (
    <div>
        <h2>uselayoutEffect</h2>
        <p>Nome:{name}</p>
        <hr/>
    </div>
  )
}

export default HookUselayoutEffect
