   // },[])
    useEffect(()=>{
        console.log("Premium numbers foi alterado")
    },[premiumNumbrs]);

  return (
    <div>
        <h2>useMemo</h2>
        <input type="text" onChange={(e)=> setNumber(e.target.value)} />
        {premiumNumbrs.includes(number)? <p>Acertou o numero</p>:""}
