    const[name,setName] = useState("Priscila")
    // 2-usestate e input
    const [age,setAge] = useState(18);

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(age);
    }
    const changeName = () =>{
        userNome = "Dani"

        setName("maia")
        console.log(userNome)
        console.log(name)
    }
  return (
    <div>
        {/*1 usestate*/}
        <h2>useestate</h2>
        <p>Variavel:{userNome}</p>
        <p>Usestate:{name}</p>
         <button onClick={changeName}>Mudar Nomes!</button>
         <p>Digite sua Idade</p>
         <form>
          <input type="text" 
          value={age} 
          onChange={(e)=> setAge(e.target.value)}
          />
          <input type="submit" value="Enviar" />
         </form>
         <p>VocÃª tem{age}Anos!</p>
         <hr/>
    </div>
  )
}

export default HookUseState
