import {useEffect,useState} from 'react'

const HookUseEffect = () => {
    //1 useEffect sem dependencias
    useEffect(()=>{
        console.log("Estou sendo Executado")
    })
    const[number,setNumber] = useState(1)
    const changeSmething = () => {
      setNumber(number +1)
    }
    // 2- Array de deps. vazio
    useEffect(()=>{
      console.log("Serei Executou apenas uma vez ")
    },[])
  return (
    <div>
        <h2>useEffect</h2>
        <p>Number:{number} </p>
        <button onClick={changeSmething}>Executar</button>
        <hr/>
    </div>
  )
}

export default HookUseEffect
