    useEffect(()=>{
        console.log("Buscando item do BD...")

        setMyItems(getItems);
    },[getItems]);
  return (
    <div>
       {myItems &&
        myItems.map((item)=>(
            <p key={item} >{item}</p>
        ))}
    </div>
  )
}

export default List
