import { useState, useEffect, useRef } from "react"

const HookUseRef = () => {
    // 1 -UseRef
    const numberRef = useRef(0);
    const [conter, setConter] = useState(0);
    const [conterB, setConterB] = useState(0);
    useEffect(()=>{
        numberRef.current = numberRef.current +1;
        //setConter(conter +1) modo erradpo que estoura memoria
    })

    const inputRef = useRef();
    const [text,setText] = useState();
    const handleSubmit = (e) =>{
        e.preventDefault()
        console.log(inputRef);//MOdo errado
    }
    return (
        <div>
            <h2>useRef</h2>
            <p>O componente rederizou:{numberRef.current}</p>
            <p>Conter 1:{conter}</p>
            <button onClick={()=> setConter(conter +1)}>Contador 1</button>
            <p>Conter 2:{conterB}</p>
            <button onClick={()=> setConterB(conterB +1)}>Contador 1</button>
            <form onSubmit={handleSubmit}>
                <input type="text" ref={inputRef} value={text} onChange={(e)=> setText(e.target.value)} />
                <input type="submit" value="Enviar" />
            </form>
            <hr/>
        </div>
    )
}

export default HookUseRef
