import React, { useRef } from 'react'
import SomeComonent from './SomeComonent';

const HookUseImperativeHandle = () => {
    const inputRef = useRef();
  return (
    <div>
        <h2>useImperativeHadle</h2>
        <SomeComonent ref={inputRef}/>
        <button onClick={()=> inputRef.current.vatidate()}>Validate</button>
    </div>
  )
}

export default HookUseImperativeHandle
